/**
 * Catalog search types
 *
 * @category   Mage
 * @package    Mage_Adminhtml
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Storm_ElasticSearch_Model_Adminhtml_System_Config_Source_Catalog_Search_Type extends Mage_Adminhtml_Model_System_Config_Source_Catalog_Search_Type
{
    public function toOptionArray()
    {
        $options = array_merge(
            parent::toOptionArray(),
            array(
                array(
                    'value' => Storm_ElasticSearch_Model_Engine::SEARCH_TYPE_ELASTICSEARCH,
                    'label' => 'ElasticSearch'
                )
            )
        );

        return $options;
    }
}
