/**
 * Fulltext Collection
 *
 * @category    Mage
 * @package     Mage_CatalogSearch
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Storm_ElasticSearch_Model_Resource_Result_Collection extends Mage_Catalog_Model_Resource_Product_Collection
{
    protected $_api;

    /**
     * Retrieve query model object
     *
     * @return Mage_CatalogSearch_Model_Query
     */
    protected function _getQuery()
    {
        return Mage::helper('catalogsearch')->getQuery();
    }

    /**
     * @return Storm_ElasticSearch_Model_Resource_Engine
     */
    protected function _getEngine()
    {
        return Mage::helper('catalogsearch')->getEngine();
    }

    /**
     * Add search query filter
     *
     * @param string $query
     * @return Mage_CatalogSearch_Model_Resource_Fulltext_Collection
     */
    public function addSearchFilter($query)
    {
        $ids = array();
        if($result = $this->_getEngine()->search($this->_getQuery()->getStoreId(), $query)) {
            foreach($result['items'] as $item) {
                $ids[] = $item['_id'];
            }
        }

        $this->addFieldToFilter('entity_id', array('in' => $ids));
        return $this;
    }

    /**
     * Stub method for campatibility with other search engines
     *
     * @return Mage_CatalogSearch_Model_Resource_Fulltext_Collection
     */
    public function setGeneralDefaultQuery()
    {
        return $this;
    }
}
